INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
	(1, 1, '2017-11-22 10:53:56', '2017-11-22 12:53:56', 'Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a escrever!', 'Olá, mundo!', '', 'publish', 'open', 'open', '', 'ola-mundo', '', '', '2017-11-22 10:53:56', '2017-11-22 12:53:56', '', 0, 'http://laestampahome.novo.com.br/?p=1', 0, 'post', '', 1),
	(2, 1, '2017-11-22 10:53:56', '2017-11-22 12:53:56', 'Este é o exemplo de uma página. É diferente de um post de blog porque é estática e pode aparecer em menus de navegação (na maioria dos temas). A maioria das pessoas começam com uma página \'Sobre\' que as apresenta aos potenciais visitantes do site. Você pode usar algo como:\n\n<blockquote>Oi! Sou um estudante de Biologia e gosto de esportes e natureza. Nos fins-de-semana pratico futebol com meus amigos no clube local. Eu moro em Valinhos e fiz este site para falar sobre minha cidade.</blockquote>\n\n...ou algo como:\n\n<blockquote>A empresa Logos foi fundada em 1980, e tem provido o comércio local com o que há de melhor em informatização. Localizada em Recife, nossa empresa tem se destacado como um das que também contribuem para o descarte correto de equipamentos eletrônicos substituídos.</blockquote>\n\nComo um novo usuário WordPress, vá ao seu <a href="http://laestampahome.novo.com.br/wp-admin/">Painel</a> para excluir este conteúdo e criar o seu. Divirta-se!', 'Página de exemplo', '', 'trash', 'closed', 'open', '', 'pagina-exemplo__trashed', '', '', '2017-11-22 13:17:18', '2017-11-22 15:17:18', '', 0, 'http://laestampahome.novo.com.br/?page_id=2', 0, 'page', '', 0),
	(4, 1, '2017-11-22 11:40:23', '2017-11-22 13:40:23', '', 'Loja', '', 'publish', 'closed', 'closed', '', 'loja', '', '', '2017-11-22 11:40:23', '2017-11-22 13:40:23', '', 0, 'http://laestampahome.novo.com.br/loja/', 0, 'page', '', 0),
	(5, 1, '2017-11-22 11:40:23', '2017-11-22 13:40:23', '[woocommerce_cart]', 'Carrinho', '', 'publish', 'closed', 'closed', '', 'carrinho', '', '', '2017-11-22 11:40:23', '2017-11-22 13:40:23', '', 0, 'http://laestampahome.novo.com.br/carrinho/', 0, 'page', '', 0),
	(6, 1, '2017-11-22 11:40:24', '2017-11-22 13:40:24', '[woocommerce_checkout]', 'Finalizar compra', '', 'publish', 'closed', 'closed', '', 'finalizar-compra', '', '', '2017-11-22 11:40:24', '2017-11-22 13:40:24', '', 0, 'http://laestampahome.novo.com.br/finalizar-compra/', 0, 'page', '', 0),
